import React, { Component } from "react"
import NavbarLanding from "../components/Navbar"
import {Button, FormControl, InputGroup} from "react-bootstrap"
import moment from "moment-timezone"

class Landing extends Component{
    state   = {
        currentCity:"",
        cityLivedIn:[],
        cityToSendWork:[]

    }
    searchCitySearchButton = (event) =>{
        event.preventDefault()
        console.log(this.state.currentCity)
        console.log(moment().tz(`America/${this.state.currentCity}`).format())

    }
    searchCityEnter = event =>{
        if(event.key === 'Enter'){
            console.log("Enter pressed")
        }
    }
    handleInputChange = event =>{
        const {name,value} = event.target;
        this.setState({
            [name]: value
        })
    }
    render(){
        return(
        <div>
            <NavbarLanding/>
            <InputGroup className="mb-3">
                <FormControl
                name="currentCity"
                placeholder="City to look for"
                value = {this.state.currentCity}
                onKeyDown={this.searchCityEnter}
                onChange={this.handleInputChange}
                />
                <Button variant="outline-secondary" id="button-addon2" onClick={this.searchCitySearchButton}>
                Search
                </Button>
            </InputGroup>


        </div>
        )
    }
}


export default Landing;
